import React, { useState } from 'react';
import Header from './components/Header';
import Menu from './components/Menu';
import Examples from "./components/Examples";

function App() {
  const [selectedMenu, setSelectedMenu] = useState('');

  const meiji = {
      title: 'Meiji',
      players: [
          { name: 'Miyuki', position: 'Catcher' },
          // More characters...
      ]}

    const seidou = {
      title: 'Seidou',
        players: [
            { name: 'Sawamura', position: 'Pitcher' },
            // More characters...
        ]}



  //const tabsData = {
    //characters: [meiji, seidou], about: [meiji, seidou],
  //};


    const tabsData2 = {
        characters: {
            meiji: meiji,
            seidou: seidou
        },
        about: {
            meiji: meiji,
            seidou: seidou
        },
    };

  return (
      <div>
        <Header />
          <Menu menuEntry={tabsData2} onSelect={(menu) => setSelectedMenu(menu)} />
          {Object.entries(tabsData2[selectedMenu] || {}).map(([key, value], index) => (
              <Examples key={index} tab={{ [key]: value }} />
          ))}
      </div>
  );
}

export default App;
