            meiji: MEIJI,
            seidou: SEIDOU
        },
        about: {
            meiji: MEIJI,
            seidou: SEIDOU
        },
    };
*/
  return (
      <div>
          <main>
        <Header />
              <Menu menuEntry={mainMenu} onSelect={handleSelect} selected={selectedMenu} />

              {selectedMenu && mainMenu[selectedMenu] ? (
              <Examples tab={mainMenu[selectedMenu]} />
          ) : (
              <div></div>
          )}
              <Footer />
          </main>

      </div>
  );
}

export default App;
