import React, { useState } from 'react';
import Header from './components/Header';
import Menu from './components/Menu';
import Examples from "./components/Examples";

function App() {
  const [selectedMenu, setSelectedMenu] = useState('');

  const meiji = {
      title: 'Meiji',
      groups: {
          team: { title:'Team',
              players: [
                  { name: 'Miyuki', role: 'Catcher', details: 'Pretty Boy', photo: '/iwy-logo.png'},
                  // More characters...
              ]},
      coaches: {title: 'Coaches',
          players: [
        { name: 'Suzuki', position: 'Bullpen Coach' },
        { name: 'Yamamoto', position: 'Trainer' }
    ]},
      managers: {
              title: 'Managers',
          players: [
              { name: 'Watanabe Hisashi', position: 'Analyst' },
              // More characters...
          ]}
      }}


    const seidou = {
      title: 'Seidou',
        players: [
            { name: 'Sawamura', position: 'Pitcher' },
            // More characters...
        ]}


    const tabsData2 = {
        characters: {
            meiji: meiji,
            seidou: seidou
        },
        about: {
            meiji: meiji,
            seidou: seidou
        },
    };

  return (
      <div>
          <main>
        <Header />
          <Menu menuEntry={tabsData2} onSelect={(menu) => setSelectedMenu(menu)} />
          {selectedMenu && tabsData2[selectedMenu] ? (
              <Examples tab={tabsData2[selectedMenu]} />
          ) : (
              <div>Select a menu item</div>
          )}
          </main>
      </div>
  );
}

export default App;
