function App() {
  const [selectedMenu, setSelectedMenu] = useState('');

  const meiji = {characters: [
          { name: 'Miyuki', position: 'Catcher' },
          // More characters...
      ]}

    const seidou = {players: [
            { name: 'Sawamura', position: 'Pitcher' },
            // More characters...
        ]}

  const tabsData = {
    players: [meiji, seidou], characters2: [meiji, seidou],
  };

  return (
      <div>
        <Header />
        <Menu onSelect={(menu) => setSelectedMenu(menu)} />
        {tabsData[selectedMenu]?.map((tab, index) => (
            <Tab key={index} players={tab.players} />
        ))}
      </div>
  );
}

export default App;
