import React, { useState } from 'react';
import './Stats.css'; // Import the CSS file for styling

const pitches = [
    {
        name: 'Fastball',
        description: 'A pitch thrown at high speed.',
        photo: 'https://example.com/fastball.jpg'
    },
    {
        name: 'Curveball',
        description: 'A pitch that curves as it approaches the batter.',
        photo: 'https://example.com/curveball.jpg'
    },
    {
        name: 'Slider',
        description: 'A pitch that slides away from the batter.',
        photo: 'https://example.com/slider.jpg'
    }
    // Add more pitches here if needed
];

const Stats = () => {
    const [isExpanded, setIsExpanded] = useState(false); // To handle expanding/collapsing
    const [selectedPitch, setSelectedPitch] = useState(null); // To handle selected pitch

    const toggleExpand = () => {
        setIsExpanded(!isExpanded);
    };

    const handlePitchSelect = (pitch) => {
        setSelectedPitch(pitch);
    };

    return (
        <div className="stats-container">
            <div className="stats-header" onClick={toggleExpand}>
                <h2>Stats</h2>
                <span>{isExpanded ? '-' : '+'}</span>
            </div>

            {isExpanded && (
                <div className="stats-content">
                    {/* Left panel: List of pitches */}
                    <div className="pitch-list">
                        <ul>
                            {pitches.map((pitch, index) => (
                                <li key={index} onClick={() => handlePitchSelect(pitch)}>
                                    {pitch.name}
                                </li>
                            ))}
                        </ul>
                    </div>

                    {/* Right panel: Selected pitch details */}
                    <div className="pitch-details">
                        {selectedPitch ? (
                            <>
                                <h3>{selectedPitch.name}</h3>
                                <img src={selectedPitch.photo} alt={selectedPitch.name} />
                                <p>{selectedPitch.description}</p>
                            </>
                        ) : (
                            <p>Select a pitch to see details</p>
                        )}
                    </div>
                </div>
            )}
        </div>
    );
};

export default Stats;
