// src/components/Stats.js
import React, { useState } from 'react';

function Stats({ stats }) {
    const [selectedPitch, setSelectedPitch] = useState(null);

    return (
        <div className="stats">
            <div className="pitches">
                {stats.pitch.map((pitch, index) => (
                    <button key={index} onClick={() => setSelectedPitch(pitch)}>
                        {pitch.name}
                    </button>
                ))}
            </div>
            {selectedPitch && (
                <div className="selected-pitch">
                    <h2>{selectedPitch.name}</h2>
                    <img src={selectedPitch.photo} alt={selectedPitch.name} />
                    <p>{selectedPitch.description}</p>
                </div>
            )}
        </div>
    );
}

export default Stats;
