import React, { useState } from 'react';
import Header from './components/Header';
import Menu from './components/Menu';
import Examples from "./components/Examples";
import {MEIJI, MAIN, SEIDOU, OTHERS} from './characters.js';
import Footer from "./components/Footer";
import Tab from "./components/Tab";
import Tabs from "./components/Tabs";

function App() {
    const [selectedMenu, setSelectedMenu] = useState(null);

    const handleSelect = (key) => {
        if (selectedMenu === key) {
            setSelectedMenu(null);  // Deselect if the same button is clicked
        } else {
            setSelectedMenu(key);  // Select the new button
        }
    };

    const mainMenu = {
        story: {},
        characters: {
            main: MAIN,
            meiji: MEIJI,
            others: OTHERS
        },
        trivia: {}
    };
    /*
        const tabsData2 = {
            characters: {
                main: MAIN,
                meiji: MEIJI,
                seidou: SEIDOU
            },
            about: {
                meiji: MEIJI,
                seidou: SEIDOU
            },
        };
    */
    return (
        <div>
            <main>
                <Header/>
                <Menu menuEntry={mainMenu} onSelect={handleSelect} selected={selectedMenu}/>
                {selectedMenu === 'characters' ? <Examples tab={mainMenu[selectedMenu]}/> : <Tabs/>}
                <div></div>
                <Footer/>
            </main>
        </div>
    );
}

export default App;
